INSERT INTO `wp_posts` VALUES("53", "1", "2013-12-18 08:41:08", "2013-12-18 08:41:08", "Amber Oaks Apartments (originally Country Scene Apartments) is a 96 unit garden\r\n\r\napartment community constructed in 1986 located in Durham, NC.\r\n\r\nThis property was acquired in late 2012. Immediately after closing, ownership embarked on a $450,000 improvement program addressing deferred maintenance as\r\n\r\nwell as performing interior unit renovations.\r\n\r\nSpecific asset management strategies employed to enhance revenue and tenant\r\n\r\nquality:\r\n<h2>UNIT UPGRADES</h2>\r\n<p class=\"nopadding\">Full Renovation Strategy: Fully renovate units as tenants vacate</p>\r\n<p class=\"nopadding\">Partial Renovation Strategy: Offer customized renovation packages to existing</p>\r\ntenants who have a good payment history. This program allows for a faster execution\r\n\r\nof the renovation program while simultaneously reducing downtime resulting in a\r\n\r\nlower vacancy rate.\r\n<h2>EVICTIONS/DELINQUENCY</h2>\r\n<p class=\"nopadding\">Enact an aggressive eviction program to remove not only non-paying tenants but</p>\r\nsystematic late payers.\r\n<h2>AUTOMATION OF RUBS SYSTEM</h2>\r\n<p class=\"nopadding\">Exchanged manually read water meters to a wireless automated system. This system</p>\r\nresulted in an increase in monthly collections of water &amp; sewer charges as well as\r\n<h2>CURB APPEAL ENHANCEMENT</h2>\r\n<p class=\"nopadding\">Resurfacing the parking field, installing a new playground, converting the tennis</p>\r\ncourt into sport and restoring and reopening the pool after being closed for 5 years.\r\n</p>", "Amber Oaks Copy", "", "publish", "closed", "closed", "", "amber-oaks-2", "", "", "2013-12-20 21:39:16", "2013-12-20 21:39:16", "", "0", "http://www.saratoga.demo/case_study/amber-oaks-2/", "0", "case_study", "", "0");
